mapper.insert:
 		INSERT INTO USERS
	 			(
	 				 USERID
	 				,NAME
	 				,ALIAS
	 				,PASS
	 				,ADDR1
	 				,ADDR2
	 				,ZIPCD
	 				,BIRTH
	 				,PATH
	 				,FILENAME
	 			)
 					 VALUES
 				(
 					 #{userId}
 					,#{name}
 					,#{alias}
 					,#{pass}
 					,#{addr1}
 					,#{addr2}
 					,#{zipcd}
 					,#{birth}
 					,#{path, jdbcType=VARCHAR}
 					,#{filename, jdbcType=VARCHAR}
 				)

mapper.update:
UPDATE	USERS
SET		 NAME = #{name}
		,ALIAS = #{alias}
		,PASS = #{pass}
		,ADDR1 = #{addr1}
		,ADDR2 = #{addr2}
		,ZIPCD = #{zipcd}
		,BIRTH = #{birth}
		,PATH = #{path, jdbcType=VARCHAR}
		,FILENAME = #{filename, jdbcType=VARCHAR}
WHERE	USERID = #{userId}

UPDATE	USERS	
SET		PASS = #{pass}
WHERE	USERID = #{userId}
